DROP TABLE MESSAGE;
CREATE TABLE MESSAGE ( 
    ID INT NOT NULL,
    TYPE INT NOT NULL,
    RANK INT NOT NULL,
    EFFDT DATE NOT NULL,
    ENDDT DATE,
    TITLE VARCHAR (50) NOT NULL, 
    TEXT  VARCHAR (255) NOT NULL,
    LINK  VARCHAR (100),  
    TARGET VARCHAR (75),
    STATUS VARCHAR (3) NOT NULL);
    
CREATE UNIQUE INDEX MESSAGE ON
    MESSAGE(ID, TYPE, RANK, EFFDT);

COMMIT;
